'use strict';
class TabsSwitcher {
	constructor(groupNode) {
		this.tablistNode = groupNode;
		this.tabs = [];
		this.firstTab = null;
		this.lastTab = null;
		this.tabs = Array.from(this.tablistNode.querySelectorAll('[role=tab]'));
		this.tabpanels = [];

		for (let i = 0; i < this.tabs.length; i++) {
			const tab = this.tabs[i];
			const tabPanel = document.getElementById(tab.getAttribute('aria-controls'));

			tab.setAttribute('aria-selected', 'false');
			this.tabpanels.push(tabPanel);

			tab.addEventListener('keydown', this.onKeydown.bind(this));
			tab.addEventListener('click', this.onClick.bind(this));

			if (!this.firstTab) {
				this.firstTab = tab;
			}
			this.lastTab = tab;
		}

		//this.setSelectedTab(this.firstTab, false);
	}

	 setSelectedTab(currentTab, setFocus) {
		if (typeof setFocus !== 'boolean') {
			setFocus = true;
		}
		for (let i = 0; i < this.tabs.length; i++) {
			const tab = this.tabs[i];
			if (currentTab === tab) {
				//$('#collectionSpotlightCarousel' + tab.dataset.carouselid).jcarousel('reload');
				$(tab).attr('aria-selected', 'true');
				$(tab).addClass('active');
				//this.tabs[i].classList.add('active');
				if (setFocus) {
					tab.focus();
				}
			} else {
				//$('#collectionSpotlightCarousel' + tab.dataset.carouselid).jcarousel('reload');
				$(tab).attr('aria-selected', 'false');
				$(tab).removeClass('active');
				//this.tabs[i].classList.remove('active');
			}
		}
	}

	setSelectedToPreviousTab(currentTab) {
		let index;

		if (currentTab === this.tabs.firstTab) {
			this.setSelectedTab(this.tabs.lastTab);
		} else {
			index = this.tabs.indexOf(currentTab);
			this.setSelectedTab(this.tabs[index - 1]);
		}
	}

	setSelectedToNextTab(currentTab) {
		let index;

		if (currentTab === this.tabs.lastTab) {
			this.setSelectedTab(this.tabs.firstTab);
		} else {
			index = this.tabs.indexOf(currentTab);
			this.setSelectedTab(this.tabs[index + 1]);
		}
	}

	onKeydown(event) {
		const tgt = event.currentTarget;
		let flag = false;

		switch (event.key) {
			case 'ArrowLeft':
				this.setSelectedToPreviousTab(tgt);
				flag = true;
				break;

			case 'ArrowRight':
				this.setSelectedToNextTab(tgt);
				flag = true;
				break;

			case 'Home':
				this.setSelectedTab(this.tabs.firstTab);
				flag = true;
				break;

			case 'End':
				this.setSelectedTab(this.tabs.lastTab);
				flag = true;
				break;

			default:
				break;
		}

		if (flag) {
			event.stopPropagation();
			event.preventDefault();
		}
	}

	onClick(event) {
		this.setSelectedTab(event.currentTarget);
	}
}
AspenDiscovery.CookieConsent = (function() {
    return {
        cookieAgree: function(props) {
            if (props == 'all') {
                var cookieString = {
                    Essential:1,
                    Analytics:1,
                };
            } else if (props == 'essential') {
                var cookieString = {
                    Essential:1,
                    Analytics:0,
                };
            }
            $('.stripPopup').hide();
            $('.modal').modal('hide');
            //set cookie and update db (if logged in) with AJAX
			var url = Globals.path + "/AJAX/JSON";
			var params =  {
				method : 'saveCookiePreference',
                cookieEssential: cookieString['Essential'],
                cookieAnalytics: cookieString['Analytics'],
			};
			$.getJSON(url, params,
				function(data) {
					if (data.success) {
						if (data.message.length > 0){
							//User was logged in, show a message about how to update
							AspenDiscovery.showMessage('Success', data.message, true, true);
						}else{
							//Refresh the page
							// noinspection SillyAssignmentJS
							window.location.href = window.location.href;
						}
					} else {
						AspenDiscovery.showMessage("Error", data.message);
					}
				}
			).fail(AspenDiscovery.ajaxFail);
			return false;
        },
        cookieDisagree: function() {
            AspenDiscovery.showMessage("Cookie Policy", Globals.cookiePolicyHTML);
            return;
        },
        fetchUserCookie: function(Values) {
            document.cookie = 'cookieConsent' + '=' + encodeURIComponent(Values) + ';  path=/';
            return;
        },
    }
}(AspenDiscovery.CookieConsent));


